import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './shoppingcartsize.css';

class ShoppingCartSize extends Component {
  constructor(props) {
    super(props);
    const size = JSON.parse(localStorage.getItem('cart')).length;
    this.state = { cartSize: size };
  }

  render() {
    const { cartSize } = this.state;
    return (
      <Link to="/shoppingcart">
        <button
          type="button"
          className="cart"
          data-testid="shopping-cart-button"
        >
          <p>{cartSize}</p>
        </button>
      </Link>
    );
  }
}


export default ShoppingCartSize;
