import React, { Component } from 'react';

class AddCartButton extends Component {
  constructor(props) {
    super(props);

    this.addToCart = this.addToCart.bind(this);
    this.checkQuantity = this.checkQuantity.bind(this);
  }

  addToCart() {
    const { product } = this.props;
    const available = (product.quantity < product.available_quantity);
    const totalItems = parseInt(localStorage.getItem('totalItems'), 10) || 0;
    const cart = JSON.parse(localStorage.getItem('cart'));
    // carrinho vazio
    if (!cart) {
      localStorage.setItem('totalItems', totalItems + 1);
      localStorage.setItem('cart', JSON.stringify([product]));
    }
    // já tem o item e aumentar quantidade
    if (localStorage.cart.includes(product.id) && available) {
      const index = cart.findIndex((item) => item.id === product.id);
      cart[index].quantity += 1;
      localStorage.setItem('totalItems', totalItems + 1);
      localStorage.setItem('cart', JSON.stringify(cart));
    }
    // carrinho existe, mas item novo
    localStorage.setItem('totalItems', totalItems + 1);
    localStorage.setItem('products', JSON.stringify([...cart, product]));
  }

  render() {
    return (
      <button type="button" onClick={this.addToCart}>Adicionar ao Carrinho!</button>
    );
  }
}

export default AddCartButton;
