import React, { Component } from 'react';
import * as API from '../../services/api';
import ProductList from '../../components/ProductList/ProductList';
import ShoppingCartSize from '../../components/ShoppingCartSize/ShoppingCartSize';
import './productlistpage.css';

class ProductListPage extends Component {
  constructor(props) {
    super(props);
    const size = (JSON.parse(localStorage.getItem('cart')) === 'asa' ? '' : JSON.parse(localStorage.getItem('cart')).length);
    this.state = {
      categories: [],
      query: '',
      categoryID: '',
      productsList: [],
      cartSize: size,
    };
    this.handleRadio = this.handleRadio.bind(this);
    this.handleQueryButton = this.handleQueryButton.bind(this);
    this.updateCart = this.updateCart.bind(this);
  }

  componentDidMount() {
    API.getCategories().then((categories) => this.setState({ categories }));
  }

  updateCart() {
    this.setState({ cartSize: JSON.parse(localStorage.getItem('cart')).length });
  }

  categoryList(id, name) {
    return (
      <div key={id}>
        <input
          type="radio"
          id={id}
          value={id}
          onClick={(e) => this.handleRadio(e)}
          name="category"
          key={id}
          data-testid="category"
        />
        <label htmlFor={id}>{name}</label>
      </div>
    );
  }


  handleRadio(event) {
    const { query } = this.state;
    this.setState({ categoryID: event.target.value });
    API.getProductsFromCategoryAndQuery(event.target.value, query)
      .then((r) => this.setState({ productsList: r.results }));
  }

  handleQueryButton() {
    const { query, categoryID } = this.state;
    API.getProductsFromCategoryAndQuery(categoryID, query)
      .then((r) => this.setState({ productsList: r.results }));
  }

  renderHeader() {
    const { query, cartSize } = this.state;
    return (
      <div className="header">
        <input
          type="text"
          data-testid="query-input"
          value={query}
          onChange={(e) => this.setState({ query: e.target.value })}
        />
        <button type="button" data-testid="query-button" onClick={this.handleQueryButton}>
          BUSCA
        </button>
        <ShoppingCartSize cartSize={cartSize} />
      </div>
    );
  }

  render() {
    const { categories, productsList, cartSize } = this.state;
    return (
      <div className="container">
        {this.renderHeader()}
        <div className="inner-container">
          <div className="categories-container">
            {categories.map(({ id, name }) => this.categoryList(id, name))}
          </div>
          <ProductList productsList={productsList} updateCart={this.updateCart} cartSize={cartSize} />
        </div>
      </div>
    );
  }
}

export default ProductListPage;
